-- EDOP Matrix Schema
-- Creates tables for World Heritage site environmental signature matrix
-- Run with: psql -h localhost -p 5435 -U postgres -d edop < sql/edop_matrix_schema.sql

-- Drop existing tables if they exist (in reverse dependency order)
DROP TABLE IF EXISTS edop_matrix CASCADE;
DROP TABLE IF EXISTS edop_wh_sites CASCADE;
DROP TABLE IF EXISTS edop_norm_ranges CASCADE;

--------------------------------------------------------------------------------
-- 1. Normalization ranges table (single row with global min/max for each field)
--------------------------------------------------------------------------------
CREATE TABLE edop_norm_ranges (
    id INTEGER PRIMARY KEY DEFAULT 1 CHECK (id = 1),  -- Ensures single row

    -- A: Physiographic Bedrock
    elev_min_min DOUBLE PRECISION, elev_min_max DOUBLE PRECISION,
    elev_max_min DOUBLE PRECISION, elev_max_max DOUBLE PRECISION,
    slope_avg_min DOUBLE PRECISION, slope_avg_max DOUBLE PRECISION,
    slope_upstream_min DOUBLE PRECISION, slope_upstream_max DOUBLE PRECISION,
    stream_gradient_min DOUBLE PRECISION, stream_gradient_max DOUBLE PRECISION,
    karst_min DOUBLE PRECISION, karst_max DOUBLE PRECISION,
    karst_upstream_min DOUBLE PRECISION, karst_upstream_max DOUBLE PRECISION,

    -- B: Hydro-Climatic Baselines
    discharge_yr_min DOUBLE PRECISION, discharge_yr_max DOUBLE PRECISION,
    discharge_min_min DOUBLE PRECISION, discharge_min_max DOUBLE PRECISION,
    discharge_max_min DOUBLE PRECISION, discharge_max_max DOUBLE PRECISION,
    river_area_min DOUBLE PRECISION, river_area_max DOUBLE PRECISION,
    river_area_upstream_min DOUBLE PRECISION, river_area_upstream_max DOUBLE PRECISION,
    runoff_min DOUBLE PRECISION, runoff_max DOUBLE PRECISION,
    gw_table_depth_min DOUBLE PRECISION, gw_table_depth_max DOUBLE PRECISION,
    pct_clay_min DOUBLE PRECISION, pct_clay_max DOUBLE PRECISION,
    pct_silt_min DOUBLE PRECISION, pct_silt_max DOUBLE PRECISION,
    pct_sand_min DOUBLE PRECISION, pct_sand_max DOUBLE PRECISION,

    -- C: Bioclimatic Proxies
    temp_yr_min DOUBLE PRECISION, temp_yr_max DOUBLE PRECISION,
    temp_min_min DOUBLE PRECISION, temp_min_max DOUBLE PRECISION,
    temp_max_min DOUBLE PRECISION, temp_max_max DOUBLE PRECISION,
    precip_yr_min DOUBLE PRECISION, precip_yr_max DOUBLE PRECISION,
    aridity_min DOUBLE PRECISION, aridity_max DOUBLE PRECISION,
    wet_pct_grp1_min DOUBLE PRECISION, wet_pct_grp1_max DOUBLE PRECISION,
    wet_pct_grp2_min DOUBLE PRECISION, wet_pct_grp2_max DOUBLE PRECISION,
    permafrost_extent_min DOUBLE PRECISION, permafrost_extent_max DOUBLE PRECISION,

    -- D: Anthropocene Markers
    reservoir_vol_min DOUBLE PRECISION, reservoir_vol_max DOUBLE PRECISION,
    cropland_extent_min DOUBLE PRECISION, cropland_extent_max DOUBLE PRECISION,
    pop_density_min DOUBLE PRECISION, pop_density_max DOUBLE PRECISION,
    human_footprint_09_min DOUBLE PRECISION, human_footprint_09_max DOUBLE PRECISION,
    gdp_avg_min DOUBLE PRECISION, gdp_avg_max DOUBLE PRECISION,
    human_dev_idx_min DOUBLE PRECISION, human_dev_idx_max DOUBLE PRECISION
);

--------------------------------------------------------------------------------
-- 2. World Heritage sites table
--------------------------------------------------------------------------------
CREATE TABLE edop_wh_sites (
    site_id SERIAL PRIMARY KEY,
    id_no INTEGER UNIQUE NOT NULL,      -- UNESCO WH id
    name_en TEXT NOT NULL,
    description_en TEXT,                 -- For future embeddings
    lon DOUBLE PRECISION NOT NULL,
    lat DOUBLE PRECISION NOT NULL,
    basin_id INTEGER                     -- FK to basin08.id (nullable if no match)
);

CREATE INDEX idx_wh_sites_basin ON edop_wh_sites(basin_id);

--------------------------------------------------------------------------------
-- 3. Wide matrix table
-- Columns: site_id + 27 numerical + 15 pnv + 1519 categorical = 1562 columns
--------------------------------------------------------------------------------
